/**
 * SesKayit.jsx
 * Web Speech API ile ses kayit bileseni
 */

import React, { useState, useRef } from 'react';
import AnalizSonucu from './AnalizSonucu';
import { mockAnalizYap } from '../mockData';

const SesKayit = () => {
  const [kayitAktif, setKayitAktif] = useState(false);
  const [metinCiktisi, setMetinCiktisi] = useState('');
  const [hataVar, setHataVar] = useState('');
  const [analizYapiliyor, setAnalizYapiliyor] = useState(false);
  const [analizSonucu, setAnalizSonucu] = useState(null);
  const sesAlgilimaRef = useRef(null);

  // Web Speech API desteƒüi kontrolu
  const speechAPIDestekliMi = () => {
    return 'webkitSpeechRecognition' in window || 'SpeechRecognition' in window;
  };

  // Ses kaydini baslat
  const sesKaydiBaslat = () => {
    if (!speechAPIDestekliMi()) {
      setHataVar('Tarayƒ±cƒ±nƒ±z ses tanƒ±ma √∂zelliƒüini desteklemiyor');
      return;
    }

    // SpeechRecognition nesnesi olustur
    const SpeechRecognition = window.SpeechRecognition || window.webkitSpeechRecognition;
    sesAlgilimaRef.current = new SpeechRecognition();
    
    // Ayarlar
    sesAlgilimaRef.current.continuous = true;
    sesAlgilimaRef.current.interimResults = true;
    sesAlgilimaRef.current.lang = 'tr-TR';

    // Kayit basladiginda
    sesAlgilimaRef.current.onstart = () => {
      setKayitAktif(true);
      setHataVar('');
      setMetinCiktisi('');
      setAnalizSonucu(null); // √ñnceki sonucu temizle
      console.log('Ses kaydi basladi...');
    };

    // Sonuc alindƒ±ƒüƒ±nda
    sesAlgilimaRef.current.onresult = (event) => {
      let sonMetin = '';
      
      for (let i = event.resultIndex; i < event.results.length; i++) {
        if (event.results[i].isFinal) {
          sonMetin += event.results[i][0].transcript;
        }
      }
      
      if (sonMetin) {
        setMetinCiktisi(prev => prev + sonMetin + ' ');
      }
    };

    // Hata durumunda
    sesAlgilimaRef.current.onerror = (event) => {
      console.error('Ses tanima hatasi:', event.error);
      setHataVar(`Ses tanƒ±ma hatasƒ±: ${event.error}`);
      setKayitAktif(false);
    };

    // Kayit bittiginde
    sesAlgilimaRef.current.onend = () => {
      setKayitAktif(false);
      console.log('Ses kaydi bitti');
    };

    // Kaydƒ± baslat
    sesAlgilimaRef.current.start();
  };

  // Ses kaydini durdur
  const sesKaydiDurdur = () => {
    if (sesAlgilimaRef.current && kayitAktif) {
      sesAlgilimaRef.current.stop();
    }
  };

  // Metni analiz et (Mock API)
  const metniAnalazEt = async () => {
    if (!metinCiktisi.trim()) {
      setHataVar('Analiz i√ßin metin gerekli');
      return;
    }

    setAnalizYapiliyor(true);
    setHataVar('');

    try {
      const sonuc = await mockAnalizYap(metinCiktisi.trim());
      setAnalizSonucu(sonuc);
    } catch (error) {
      setHataVar('Analiz sƒ±rasƒ±nda hata olu≈ütu');
      console.error('Analiz hatasi:', error);
    } finally {
      setAnalizYapiliyor(false);
    }
  };

  // Yeni analiz i√ßin sƒ±fƒ±rla
  const yeniAnalizBaslat = () => {
    setMetinCiktisi('');
    setAnalizSonucu(null);
    setHataVar('');
    setAnalizYapiliyor(false);
  };

  // Eƒüer analiz sonucu varsa, onu g√∂ster
  if (analizSonucu) {
    return <AnalizSonucu sonuc={analizSonucu} onYeniAnaliz={yeniAnalizBaslat} />;
  }

  return (
    <div className="max-w-4xl mx-auto">
      <div className="bg-white rounded-xl shadow-lg p-8">
        <div className="text-center">
          {/* Mikrofon ikonu */}
          <div className={`w-32 h-32 rounded-full flex items-center justify-center mx-auto mb-6 transition-colors duration-300 ${
            kayitAktif ? 'bg-red-100 animate-pulse' : 
            analizYapiliyor ? 'bg-yellow-100 animate-pulse' : 'bg-blue-100'
          }`}>
            <span className="text-6xl">
              {analizYapiliyor ? 'üß†' : kayitAktif ? 'üî¥' : 'üéôÔ∏è'}
            </span>
          </div>
          
          {/* Ba≈ülƒ±k */}
          <h2 className="text-2xl font-semibold text-gray-800 mb-4">
            {analizYapiliyor ? 'Analiz Ediliyor...' :
             kayitAktif ? 'Dinliyorum...' : 'Konu≈ümaya Ba≈ülayƒ±n'}
          </h2>
          
          {/* A√ßƒ±klama */}
          <p className="text-gray-600 mb-8">
            {analizYapiliyor ? 'AI konu≈ümanƒ±zƒ± analiz ediyor, l√ºtfen bekleyin...' :
             kayitAktif ? 'Konu≈üun, s√∂zleriniz metne d√∂n√º≈üt√ºr√ºl√ºyor...' : 
             'Mikrofon butonuna basarak konu≈ümanƒ±zƒ± kaydedin ve AI analizini alƒ±n'}
          </p>
          
          {/* Kayƒ±t Butonu */}
          {!analizYapiliyor && (
            <button 
              onClick={kayitAktif ? sesKaydiDurdur : sesKaydiBaslat}
              className={`font-semibold py-3 px-8 rounded-lg transition-colors duration-200 ${
                kayitAktif 
                  ? 'bg-red-500 hover:bg-red-600 text-white' 
                  : 'bg-blue-500 hover:bg-blue-600 text-white'
              }`}
            >
              {kayitAktif ? 'üõë Kaydƒ± Durdur' : 'üé§ Ses Kaydƒ±nƒ± Ba≈ülat'}
            </button>
          )}

          {/* Loading Bar - Analiz sƒ±rasƒ±nda */}
          {analizYapiliyor && (
            <div className="w-full bg-gray-200 rounded-full h-2 mb-4">
              <div className="bg-blue-500 h-2 rounded-full animate-pulse" style={{width: '70%'}}></div>
            </div>
          )}

          {/* Hata Mesajƒ± */}
          {hataVar && (
            <div className="mt-6 p-4 bg-red-100 border border-red-400 text-red-700 rounded-lg">
              ‚ö†Ô∏è {hataVar}
            </div>
          )}

          {/* Metin √áƒ±ktƒ±sƒ± */}
          {metinCiktisi && !analizYapiliyor && (
            <div className="mt-8">
              <h3 className="text-lg font-semibold text-gray-800 mb-4">
                Konu≈üma Metni:
              </h3>
              <div className="bg-gray-50 border rounded-lg p-4 text-left mb-4">
                <p className="text-gray-700 whitespace-pre-wrap">
                  {metinCiktisi}
                </p>
              </div>
              
              {/* Analiz Butonu */}
              <div className="flex gap-4 justify-center">
                <button 
                  className="bg-green-500 hover:bg-green-600 text-white font-semibold py-2 px-6 rounded-lg transition-colors duration-200"
                  onClick={metniAnalazEt}
                  disabled={analizYapiliyor}
                >
                  üìä AI ile Analiz Et
                </button>
                
                <button 
                  className="bg-gray-500 hover:bg-gray-600 text-white font-semibold py-2 px-6 rounded-lg transition-colors duration-200"
                  onClick={yeniAnalizBaslat}
                >
                  üîÑ Temizle
                </button>
              </div>
            </div>
          )}
        </div>
      </div>
    </div>
  );
};

export default SesKayit;
